let name = "gaze_marker.webm";
let path = "modules/fu-azure-compendia/assets/tiles/" + name;

let token = canvas.tokens.controlled[0];
let position = token.position;
let offset = 25;

let tileData = {
  x: position.x, 
  y: position.y + offset,
  width: 100,
  height: 100,
  texture: {src: path}
};
canvas.scene.createEmbeddedDocuments("Tile",[tileData]);
